// src/components/GoogleAuth.js
import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { auth, GoogleAuthProvider, signInWithPopup } from 'firebase/auth';
import { UserContext } from '../DataManipulation/UserContext';
import Questionario from './Questionario';
import './GoogleAuth.css';
import EditarUsuario from './EditarUsuario';

function GoogleAuth() {
  const { user } = useContext(UserContext); // Obtenha o usuário do contexto
  const [questionarioCompleto, setQuestionarioCompleto] = useState(false);
  const [mostrarModal, setMostrarModal] = useState(false);
  const [mostrarEditar, setMostrarEditar] = useState(false);

  useEffect(() => {
    if (user && user.uid) {
      // Busca as informações do usuário no servidor
      axios.get(`http://localhost:8080/contas/user?uid=${user.uid}`)
        .then((response) => {
          setQuestionarioCompleto(response.data.questionarioCompleto);
          if (!response.data.questionarioCompleto) {
            setMostrarModal(true);
          }
        })
        .catch((error) => {
          console.error('Erro ao buscar informações do usuário:', error);
        });
    }
  }, [user]);

  const Login = async () => {
    try {
      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);
      const currentUser = result.user;

      if (currentUser) {
        const idToken = await currentUser.getIdToken(true);
        const response = await axios.post('http://localhost:8080/contas/login', { idToken });
        console.log('Login bem-sucedido:', response.data);
      }
    } catch (error) {
      console.error('Erro ao fazer login:', error);
    }
  };

  const logout = async () => {
    try {
      await axios.post('http://localhost:8080/contas/logout');
      await auth.signOut();
    } catch (error) {
      console.error('Erro ao fazer logout:', error);
    }
  };

  const handleQuestionarioEnviado = () => {
    setQuestionarioCompleto(true);
    setMostrarModal(false);
    setMostrarEditar(true);
    alert('Questionário enviado com sucesso!');
  };

  return (
    <div>
      {user ? (
        <div className="usuario-container">
          <div>
            {user.photoURL && (
              <img className="usuario-foto" src={user.photoURL} alt="Foto do usuário" />
            )}
          </div>
          <div className="usuario-info">
            <h2>{user.displayName || 'Usuário'}</h2>
            <p><strong>Email:</strong> {user.email}</p>
            <button onClick={logout}>Logout</button>

            {questionarioCompleto ? (
              <button onClick={() => setMostrarEditar(true)}>Editar Usuário</button>
            ) : (
              <button onClick={() => setMostrarModal(true)}>Preencher Questionário</button>
            )}

            {mostrarEditar && (
              <EditarUsuario uid={user.uid} onClose={() => setMostrarEditar(false)} />
            )}

            {mostrarModal && !questionarioCompleto && (
              <div className="modal-overlay">
                <div className="modal-content">
                  <div className="modal-header">
                    <h2>Preencha o Questionário</h2>
                  </div>
                  <Questionario uid={user.uid} onQuestionarioEnviado={handleQuestionarioEnviado} />
                </div>
              </div>
            )}
          </div>
        </div>
      ) : (
        <button onClick={Login}>Login com Google</button>
      )}
    </div>
  );
}

export default GoogleAuth;
