// src/DataManipulation/DetalhesDesafio.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function DetalhesDesafio() {
  const { id } = useParams(); // Obtendo o ID do desafio da URL
  const [desafio, setDesafio] = useState(null);
  const [solucoes, setSolucoes] = useState([]);

  // Função para carregar os detalhes do desafio
  useEffect(() => {
    if (id) {
      // Requisição para obter os detalhes do desafio
      axios.get(`http://localhost:8080/desafios/${id}`)
        .then((response) => {
          setDesafio(response.data);
        })
        .catch((error) => {
          console.error('Erro ao carregar o desafio:', error);
        });

      // Requisição para obter as soluções do desafio
      axios.get(`http://localhost:8080/desafios/solucoes/${id}`)
        .then((response) => {
          setSolucoes(response.data);
        })
        .catch((error) => {
          console.error('Erro ao carregar as soluções:', error);
        });
    }
  }, [id]);

  return (
    <div>
      {desafio ? (
        <div>
          <h2>Detalhes do Desafio</h2>
          <p><strong>Título:</strong> {desafio.titulo}</p>
          <p><strong>Descrição:</strong> {desafio.descricao}</p>
        </div>
      ) : (
        <p>Carregando detalhes do desafio...</p>
      )}

      <h3>Soluções</h3>
      {solucoes.length > 0 ? (
        <ul>
          {solucoes.map((solucao) => (
            <li key={solucao.id}>
              <p>{solucao.descricao}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>Não há soluções para este desafio ainda.</p>
      )}
    </div>
  );
}

export default DetalhesDesafio;
